import React, { useState, useEffect } from 'react';

const SessionManager = ({ contract, sessions, loading, onSessionUpdated }) => {
  const [selectedSession, setSelectedSession] = useState(null);
  const [sessionCandidates, setSessionCandidates] = useState([]);
  const [actionLoading, setActionLoading] = useState('');
  const [error, setError] = useState('');

  const now = new Date();

  // ✅ FIXED Filtering logic with proper session states
  const pendingSessions = sessions.filter(
    s => now < s.startTime && !s.isActive
  );

  const activeSessions = sessions.filter(
    s => s.isActive && now >= s.startTime && now <= s.endTime
  );

  const expiredSessions = sessions.filter(
    s => s.isActive && now > s.endTime
  );

  const completedSessions = sessions.filter(
    s => !s.isActive && now > s.endTime
  );

  const loadSessionCandidates = async (sessionId) => {
    try {
      const candidates = await contract.getSessionCandidates(sessionId);
      setSessionCandidates(candidates.map(c => ({
        id: c.id.toString(),
        name: c.name,
        voteCount: c.voteCount.toString(),
        isActive: c.isActive
      })));
    } catch (error) {
      console.error('Error loading candidates:', error);
      setSessionCandidates([]);
    }
  };

  const selectSession = async (session) => {
    setSelectedSession(session);
    await loadSessionCandidates(session.id);
  };

  const startSession = async (sessionId) => {
    try {
      setActionLoading(`start-${sessionId}`);
      setError('');

      const transaction = await contract.startSession(sessionId);
      await transaction.wait();

      alert('✅ Session started successfully!');
      onSessionUpdated();
    } catch (error) {
      console.error('Error starting session:', error);
      setError(error.message || 'Failed to start session');
    } finally {
      setActionLoading('');
    }
  };

  const endSession = async (sessionId) => {
    try {
      setActionLoading(`end-${sessionId}`);
      setError('');

      const transaction = await contract.endSession(sessionId);
      await transaction.wait();

      alert('✅ Session ended successfully!');
      onSessionUpdated();
    } catch (error) {
      console.error('Error ending session:', error);
      setError(error.message || 'Failed to end session');
    } finally {
      setActionLoading('');
    }
  };

  const formatDateTime = (date) => {
    return date.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getSessionStatus = (session) => {
    const now = new Date();

    if (now < session.startTime) {
      return { text: 'Pending', class: 'status-pending' };
    }

    if (session.isActive) {
      if (now > session.endTime) {
        return { text: 'Expired', class: 'status-expired' };
      }
      return { text: 'Active', class: 'status-active' };
    } 
    else {
      if (now > session.endTime) {
        return { text: 'Completed', class: 'status-completed' };
      }
      return { text: 'Inactive', class: 'status-inactive' };
    }
  };

  const canStartSession = (session) => {
    const now = new Date();
    return !session.isActive && now >= session.startTime && now <= session.endTime;
  };

  const canEndSession = (session) => {
    return session.isActive;
  };

  const getTimeUntilStart = (startTime) => {
    const now = new Date();
    const diff = startTime - now;

    if (diff <= 0) return 'Ready to start';

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

    if (hours > 24) {
      const days = Math.floor(hours / 24);
      return `Starts in ${days} days`;
    }

    return `Starts in ${hours}h ${minutes}m`;
  };

  if (loading) {
    return (
      <div className="session-manager">
        <div className="loading-content">
          <div className="spinner"></div>
          <h3>Loading sessions...</h3>
        </div>
      </div>
    );
  }

  return (
    <div className="session-manager">
      <div className="section-header">
        <h2>📊 Session Management</h2>
        <p>Manage all voting sessions, start/stop voting, and monitor progress</p>
      </div>

      {error && (
        <div className="error-message">
          ⚠️ {error}
        </div>
      )}

      <div className="sessions-overview">
        <div className="overview-stats">
          <div className="stat-card">
            <div className="stat-number">{sessions.length}</div>
            <div className="stat-label">Total Sessions</div>
          </div>
          <div className="stat-card pending">
            <div className="stat-number">{pendingSessions.length}</div>
            <div className="stat-label">Pending Sessions</div>
          </div>
          <div className="stat-card active">
            <div className="stat-number">{activeSessions.length}</div>
            <div className="stat-label">Active Sessions</div>
          </div>
          <div className="stat-card completed">
            <div className="stat-number">{completedSessions.length + expiredSessions.length}</div>
            <div className="stat-label">Finished Sessions</div>
          </div>
        </div>
      </div>

      <div className="sessions-content">
        <div className="sessions-list">
          {pendingSessions.length > 0 && (
            <div className="sessions-section">
              <h3 className="section-title pending">🟡 Pending Sessions</h3>
              <div className="sessions-grid">
                {pendingSessions.map(session => {
                  const status = getSessionStatus(session);
                  return (
                    <div 
                      key={session.id} 
                      className={`session-card pending ${selectedSession?.id === session.id ? 'selected' : ''}`}
                      onClick={() => selectSession(session)}
                    >
                      <div className="session-header">
                        <h4>{session.name}</h4>
                        <span className={`status-badge ${status.class}`}>
                          {status.text}
                        </span>
                      </div>

                      <div className="session-info">
                        <p><strong>📅 Start:</strong> {formatDateTime(session.startTime)}</p>
                        <p><strong>⏰ End:</strong> {formatDateTime(session.endTime)}</p>
                        <p><strong>👥 Candidates:</strong> {session.candidateCount}</p>
                        <p><strong>⏳</strong> {getTimeUntilStart(session.startTime)}</p>
                      </div>

                      <div className="session-actions">
                        {canStartSession(session) && (
                          <button
                            onClick={(e) => {
                              e.stopPropagation();
                              startSession(session.id);
                            }}
                            disabled={actionLoading === `start-${session.id}`}
                            className="btn btn-success btn-small"
                          >
                            {actionLoading === `start-${session.id}` ? '⏳' : '▶️ Start Now'}
                          </button>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {activeSessions.length > 0 && (
            <div className="sessions-section">
              <h3 className="section-title active">🟢 Active Sessions</h3>
              <div className="sessions-grid">
                {activeSessions.map(session => {
                  const status = getSessionStatus(session);
                  return (
                    <div 
                      key={session.id} 
                      className={`session-card active ${selectedSession?.id === session.id ? 'selected' : ''}`}
                      onClick={() => selectSession(session)}
                    >
                      <div className="session-header">
                        <h4>{session.name}</h4>
                        <span className={`status-badge ${status.class}`}>
                          {status.text}
                        </span>
                      </div>

                      <div className="session-info">
                        <p><strong>📅 Started:</strong> {formatDateTime(session.startTime)}</p>
                        <p><strong>⏰ Ends:</strong> {formatDateTime(session.endTime)}</p>
                        <p><strong>👥 Candidates:</strong> {session.candidateCount}</p>
                        <p><strong>🗳️ Votes:</strong> {session.totalVotes}</p>
                      </div>

                      <div className="session-actions">
                        {canEndSession(session) && (
                          <button
                            onClick={(e) => {
                              e.stopPropagation();
                              endSession(session.id);
                            }}
                            disabled={actionLoading === `end-${session.id}`}
                            className="btn btn-danger btn-small"
                          >
                            {actionLoading === `end-${session.id}` ? '⏳' : '🛑 End Session'}
                          </button>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {expiredSessions.length > 0 && (
            <div className="sessions-section">
              <h3 className="section-title expired">🔴 Expired Sessions</h3>
              <div className="sessions-grid">
                {expiredSessions.map(session => {
                  const status = getSessionStatus(session);
                  return (
                    <div 
                      key={session.id} 
                      className={`session-card expired ${selectedSession?.id === session.id ? 'selected' : ''}`}
                      onClick={() => selectSession(session)}
                    >
                      <div className="session-header">
                        <h4>{session.name}</h4>
                        <span className={`status-badge ${status.class}`}>
                          {status.text}
                        </span>
                      </div>

                      <div className="session-info">
                        <p><strong>📅 Started:</strong> {formatDateTime(session.startTime)}</p>
                        <p><strong>⏰ Expired:</strong> {formatDateTime(session.endTime)}</p>
                        <p><strong>👥 Candidates:</strong> {session.candidateCount}</p>
                        <p><strong>🗳️ Votes:</strong> {session.totalVotes}</p>
                      </div>

                      <div className="session-actions">
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            endSession(session.id);
                          }}
                          disabled={actionLoading === `end-${session.id}`}
                          className="btn btn-warning btn-small"
                        >
                          {actionLoading === `end-${session.id}` ? '⏳' : '🔒 Close Session'}
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {completedSessions.length > 0 && (
            <div className="sessions-section">
              <h3 className="section-title completed">✅ Completed Sessions</h3>
              <div className="sessions-grid">
                {completedSessions.map(session => {
                  const status = getSessionStatus(session);
                  return (
                    <div 
                      key={session.id} 
                      className={`session-card completed ${selectedSession?.id === session.id ? 'selected' : ''}`}
                      onClick={() => selectSession(session)}
                    >
                      <div className="session-header">
                        <h4>{session.name}</h4>
                        <span className={`status-badge ${status.class}`}>
                          {status.text}
                        </span>
                      </div>

                      <div className="session-info">
                        <p><strong>📅 Started:</strong> {formatDateTime(session.startTime)}</p>
                        <p><strong>⏰ Ended:</strong> {formatDateTime(session.endTime)}</p>
                        <p><strong>👥 Candidates:</strong> {session.candidateCount}</p>
                        <p><strong>🗳️ Final Votes:</strong> {session.totalVotes}</p>
                      </div>

                      <div className="session-actions">
                        <span className="session-finished">🏁 Finished</span>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {sessions.length === 0 && (
            <div className="empty-state">
              <h3>📝 No sessions created yet</h3>
              <p>Create your first voting session to get started</p>
            </div>
          )}
        </div>

        {selectedSession && (
          <div className="session-details">
            <div className="details-header">
              <h3>📋 Session Details</h3>
              <button 
                onClick={() => setSelectedSession(null)}
                className="btn btn-secondary btn-small"
              >
                ❌ Close
              </button>
            </div>

            <div className="details-content">
              <div className="detail-section">
                <h4>📊 Session Information</h4>
                <div className="detail-grid">
                  <div className="detail-item">
                    <span className="detail-label">Name:</span>
                    <span className="detail-value">{selectedSession.name}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Status:</span>
                    <span className={`detail-value ${getSessionStatus(selectedSession).class}`}>
                      {getSessionStatus(selectedSession).text}
                    </span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Start Time:</span>
                    <span className="detail-value">{formatDateTime(selectedSession.startTime)}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">End Time:</span>
                    <span className="detail-value">{formatDateTime(selectedSession.endTime)}</span>
                  </div>
                  <div className="detail-item">
                    <span className="detail-label">Total Votes:</span>
                    <span className="detail-value">{selectedSession.totalVotes}</span>
                  </div>
                </div>
              </div>

              <div className="detail-section">
                <h4>👥 Candidates</h4>
                <div className="candidates-table">
                  {sessionCandidates.length > 0 ? (
                    <table>
                      <thead>
                        <tr>
                          <th>ID</th>
                          <th>Name</th>
                          <th>Votes</th>
                          <th>Percentage</th>
                        </tr>
                      </thead>
                      <tbody>
                        {sessionCandidates.map(candidate => (
                          <tr key={candidate.id}>
                            <td>{candidate.id}</td>
                            <td>{candidate.name}</td>
                            <td>{candidate.voteCount}</td>
                            <td>
                              {selectedSession.totalVotes > 0 
                                ? Math.round((candidate.voteCount / selectedSession.totalVotes) * 100)
                                : 0
                              }%
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  ) : (
                    <p className="no-data">No candidates data available</p>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SessionManager;
