import React, { useState, useEffect } from 'react';

const OwnerPortal = ({ contract, account, onDisconnect }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sessions, setSessions] = useState([]);
  const [activeSessions, setActiveSessions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Create Session Form
  const [createForm, setCreateForm] = useState({
    name: '',
    description: '',
    startTime: '',
    endTime: '',
    candidates: ['']
  });

  useEffect(() => {
    loadData();
    setupEventListeners();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      await Promise.all([
        loadAllSessions(),
        loadActiveSessions()
      ]);
    } catch (error) {
      setError(`Error loading data: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const loadAllSessions = async () => {
    try {
      const allSessions = await contract.getAllSessions(true);
      const sessionsWithDetails = await Promise.all(
        allSessions.map(async (session) => {
          const candidates = await contract.getSessionCandidates(session.id);
          const [winnerName, winnerVotes] = await contract.getSessionWinner(session.id);
          const isActiveNow = await contract.isSessionActiveNow(session.id);

          return {
            ...session,
            id: session.id.toString(),
            startTime: new Date(Number(session.startTime) * 1000),
            endTime: new Date(Number(session.endTime) * 1000),
            createdAt: new Date(Number(session.createdAt) * 1000),
            totalVotes: session.totalVotes.toString(),
            candidates: candidates.filter(c => c.isActive).map(c => ({
              ...c,
              id: c.id.toString(),
              voteCount: c.voteCount.toString()
            })),
            winner: { name: winnerName, votes: winnerVotes.toString() },
            isActiveNow
          };
        })
      );

      setSessions(sessionsWithDetails);
    } catch (error) {
      console.error('Error loading sessions:', error);
    }
  };

  const loadActiveSessions = async () => {
    try {
      const activeSessionsData = await contract.getActiveSessions();
      setActiveSessions(activeSessionsData.map(s => ({
        ...s,
        id: s.id.toString(),
        startTime: new Date(Number(s.startTime) * 1000),
        endTime: new Date(Number(s.endTime) * 1000),
        totalVotes: s.totalVotes.toString()
      })));
    } catch (error) {
      console.error('Error loading active sessions:', error);
    }
  };

  const setupEventListeners = () => {
    contract.on("SessionCreated", (sessionId, name, creator) => {
      console.log(`Session created: ${name}`);
      loadData();
    });

    contract.on("SessionStarted", (sessionId) => {
      console.log(`Session started: ${sessionId}`);
      loadData();
    });

    contract.on("SessionStopped", (sessionId) => {
      console.log(`Session stopped: ${sessionId}`);
      loadData();
    });

    contract.on("VoteCast", (sessionId, candidateId, voter) => {
      console.log(`Vote cast in session ${sessionId}`);
      loadData();
    });

    contract.on("SessionHidden", (sessionId, hidden) => {
      console.log(`Session ${sessionId} ${hidden ? 'hidden' : 'shown'}`);
      loadData();
    });
  };

  const createSession = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);

      const startTime = Math.floor(new Date(createForm.startTime).getTime() / 1000);
      const endTime = Math.floor(new Date(createForm.endTime).getTime() / 1000);
      const validCandidates = createForm.candidates.filter(c => c.trim() !== '');

      if (validCandidates.length === 0) {
        setError('Please add at least one candidate');
        return;
      }

      const transaction = await contract.createSession(
        createForm.name,
        createForm.description,
        startTime,
        endTime,
        validCandidates
      );

      await transaction.wait();

      // Reset form
      setCreateForm({
        name: '',
        description: '',
        startTime: '',
        endTime: '',
        candidates: ['']
      });

      setActiveTab('sessions');
      alert('âœ… Session created successfully!');

    } catch (error) {
      setError(`Error creating session: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const toggleSession = async (sessionId, currentState) => {
    try {
      setLoading(true);

      if (currentState) {
        await contract.stopSession(sessionId);
      } else {
        await contract.startSession(sessionId);
      }

    } catch (error) {
      setError(`Error toggling session: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const hideSession = async (sessionId, hide) => {
    try {
      setLoading(true);
      await contract.hideSession(sessionId, hide);
    } catch (error) {
      setError(`Error hiding session: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const addCandidateField = () => {
    setCreateForm(prev => ({
      ...prev,
      candidates: [...prev.candidates, '']
    }));
  };

  const removeCandidateField = (index) => {
    setCreateForm(prev => ({
      ...prev,
      candidates: prev.candidates.filter((_, i) => i !== index)
    }));
  };

  const updateCandidate = (index, value) => {
    setCreateForm(prev => ({
      ...prev,
      candidates: prev.candidates.map((c, i) => i === index ? value : c)
    }));
  };

  const getSessionStatus = (session) => {
    const now = new Date();
    const start = session.startTime;
    const end = session.endTime;

    if (!session.isActive) return 'Inactive';
    if (now < start) return 'Scheduled';
    if (now > end) return 'Ended';
    return 'Active';
  };

  const formatDateTime = (date) => {
    return date.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const activeSessionsList = sessions.filter(s => s.isActiveNow && !s.isHidden);
  const inactiveSessionsList = sessions.filter(s => !s.isActiveNow && !s.isHidden);
  const hiddenSessionsList = sessions.filter(s => s.isHidden);

  return (
    <div className="owner-portal">
      <header className="portal-header">
        <div className="header-content">
          <h1>ðŸ‘‘ Owner Portal</h1>
          <div className="header-info">
            <span>Connected: {account.slice(0, 6)}...{account.slice(-4)}</span>
            <button onClick={onDisconnect} className="btn btn-disconnect">
              Disconnect
            </button>
          </div>
        </div>

        <nav className="portal-nav">
          <button 
            className={`nav-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            ðŸ“Š Dashboard
          </button>
          <button 
            className={`nav-btn ${activeTab === 'create' ? 'active' : ''}`}
            onClick={() => setActiveTab('create')}
          >
            âž• Create Session
          </button>
          <button 
            className={`nav-btn ${activeTab === 'sessions' ? 'active' : ''}`}
            onClick={() => setActiveTab('sessions')}
          >
            ðŸ“‹ Manage Sessions
          </button>
          <button 
