import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';

// Import your contract ABI and address
import VotingSystemData from './contracts/VotingSystem.json';

function App() {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [contract, setContract] = useState(null);
  const [candidates, setCandidates] = useState([]);
  const [account, setAccount] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isOwner, setIsOwner] = useState(false);
  const [isRegistered, setIsRegistered] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [votingActive, setVotingActive] = useState(true);
  const [winner, setWinner] = useState({ name: '', voteCount: 0 });
  const [totalVotes, setTotalVotes] = useState(0);

  useEffect(() => {
    init();
  }, []);

  const init = async () => {
    try {
      setLoading(true);
      setError('');

      // Check if MetaMask is installed
      if (typeof window.ethereum !== 'undefined') {
        // Request account access
        await window.ethereum.request({ method: 'eth_requestAccounts' });

        const provider = new ethers.BrowserProvider(window.ethereum);
        const signer = await provider.getSigner();
        const address = await signer.getAddress();

        setProvider(provider);
        setSigner(signer);
        setAccount(address);

        // Check if we have the contract data
        if (!VotingSystemData || !VotingSystemData.address || !VotingSystemData.abi) {
          throw new Error('Contract not deployed. Please run: npx hardhat run scripts/deploy.js --network development');
        }

        console.log('Contract Address:', VotingSystemData.address);
        console.log('Network:', await provider.getNetwork());

        // Initialize contract
        const votingContract = new ethers.Contract(
          VotingSystemData.address,
          VotingSystemData.abi,
          signer
        );

        setContract(votingContract);

        // Test contract connection
        try {
          const owner = await votingContract.owner();
          console.log('Contract owner:', owner);
          setIsOwner(address.toLowerCase() === owner.toLowerCase());
        } catch (contractError) {
          throw new Error(`Contract connection failed. Make sure you're connected to the right network (Ganache) and the contract is deployed. Error: ${contractError.message}`);
        }

        // Check voting status
        const votingStatus = await votingContract.votingActive();
        setVotingActive(votingStatus);

        // Check user registration and voting status
        const [registered, voted] = await votingContract.getVoterInfo(address);
        setIsRegistered(registered);
        setHasVoted(voted);

        // Load candidates, winner and total votes
        await loadCandidates(votingContract);
        await loadWinner(votingContract);
        await loadTotalVotes(votingContract);

        // Listen for events
        setupEventListeners(votingContract);

      } else {
        setError('Please install MetaMask to use this application');
      }
    } catch (error) {
      console.error('Initialization error:', error);
      setError(`Error initializing app: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const setupEventListeners = (contractInstance) => {
    contractInstance.on("VoteCast", async (voter, candidateId) => {
      console.log(`Vote cast by ${voter} for candidate ${candidateId}`);
      await loadCandidates(contractInstance);
      await loadWinner(contractInstance);
      await loadTotalVotes(contractInstance);

      // Update user status if it's the current user
      if (voter.toLowerCase() === account.toLowerCase()) {
        setHasVoted(true);
      }
    });

    contractInstance.on("VoterRegistered", (voter) => {
      console.log(`Voter registered: ${voter}`);
      if (voter.toLowerCase() === account.toLowerCase()) {
        setIsRegistered(true);
      }
    });

    contractInstance.on("VotingStatusChanged", (active) => {
      console.log(`Voting status changed: ${active}`);
      setVotingActive(active);
    });
  };

  const loadCandidates = async (contractInstance) => {
    try {
      const candidatesCount = await contractInstance.candidatesCount();

      if (candidatesCount.toString() === '0') {
        setCandidates([]);
        return;
      }

      const candidatesData = await contractInstance.getAllCandidates();
      const candidatesList = candidatesData.map((candidate) => ({
        id: candidate.id.toString(),
        name: candidate.name,
        voteCount: candidate.voteCount.toString()
      }));

      setCandidates(candidatesList);

    } catch (error) {
      console.error('Error loading candidates:', error);
      setError(`Error loading candidates: ${error.message}`);
    }
  };

  const loadWinner = async (contractInstance) => {
    try {
      const [winnerName, winnerVoteCount] = await contractInstance.getWinner();
      setWinner({
        name: winnerName,
        voteCount: winnerVoteCount.toString()
      });
    } catch (error) {
      console.error('Error loading winner:', error);
    }
  };

  const loadTotalVotes = async (contractInstance) => {
    try {
      const total = await contractInstance.getTotalVotes();
      setTotalVotes(total.toString());
    } catch (error) {
      console.error('Error loading total votes:', error);
    }
  };

  const registerSelf = async () => {
    try {
      setLoading(true);
      const transaction = await contract.registerSelf();
      await transaction.wait();
      setIsRegistered(true);
      alert('‚úÖ Successfully registered to vote!');
    } catch (error) {
      console.error('Error registering:', error);
      setError(`Error registering: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const vote = async (candidateId) => {
    try {
      if (!contract) {
        setError('Contract not initialized');
        return;
      }

      setLoading(true);
      const transaction = await contract.vote(candidateId);
      await transaction.wait();

      setHasVoted(true);
      alert('‚úÖ Vote submitted successfully!');
    } catch (error) {
      console.error('Error voting:', error);
      if (error.message.includes('You have already voted')) {
        setError('You have already voted!');
      } else if (error.message.includes('You are not registered to vote')) {
        setError('You are not registered to vote!');
      } else {
        setError(`Error voting: ${error.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const toggleVoting = async () => {
    try {
      setLoading(true);
      const transaction = await contract.toggleVoting();
      await transaction.wait();
      setVotingActive(!votingActive);
      alert(`üó≥Ô∏è Voting ${!votingActive ? 'activated' : 'deactivated'}!`);
    } catch (error) {
      console.error('Error toggling voting:', error);
      setError(`Error toggling voting: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <h2>Loading Voting System...</h2>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error">
          <h2>‚ö†Ô∏è Error</h2>
          <p>{error}</p>
          <button onClick={init} className="btn btn-primary">üîÑ Retry</button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <header className="header">
        <h1>üó≥Ô∏è Decentralized Voting System</h1>
        <div className="header-info">
          <div className="account-info">
            <p><strong>üì± Connected Account:</strong> {account.slice(0, 6)}...{account.slice(-4)}</p>
            <p><strong>üë§ Role:</strong> {isOwner ? 'üëë Owner' : 'üó≥Ô∏è Voter'}</p>
            <p><strong>üéØ Network:</strong> Ganache Local</p>
          </div>
          <div className="voting-status">
            <p><strong>Status:</strong> 
              <span className={votingActive ? 'status-active' : 'status-inactive'}>
                {votingActive ? 'üü¢ Active' : 'üî¥ Inactive'}
              </span>
            </p>
            <p><strong>Total Votes:</strong> {totalVotes}</p>
          </div>
        </div>
      </header>

      {/* Owner Controls */}
      {isOwner && (
        <section className="owner-section">
          <h2>üëë Owner Controls</h2>
          <div className="owner-controls">
            <button 
              onClick={toggleVoting}
              disabled={loading}
              className={`btn ${votingActive ? 'btn-danger' : 'btn-success'}`}
            >
              {votingActive ? 'üõë Stop Voting' : '‚ñ∂Ô∏è Start Voting'}
            </button>
          </div>
          <p className="owner-info">
            As the owner, you can start or stop the voting process at any time.
          </p>
        </section>
      )}

      {/* Voter Registration */}
      {!isRegistered && !isOwner && (
        <section className="registration-section">
          <h2>üìù Voter Registration</h2>
          <p>You need to register before you can vote in this election.</p>
          <button 
            onClick={registerSelf}
            disabled={loading}
            className="btn btn-primary btn-large"
          >
            üìù Register to Vote
          </button>
        </section>
      )}

      {/* Current Winner */}
      {winner.name && winner.name !== "No votes cast" && totalVotes > 0 && (
        <section className="winner-section">
          <h2>üèÜ Current Leader</h2>
          <div className="winner">
            <h3>üéñÔ∏è {winner.name}</h3>
            <p>{winner.voteCount} votes ({totalVotes > 0 ? Math.round((winner.voteCount / totalVotes) * 100) : 0}%)</p>
          </div>
        </section>
      )}

      {/* Candidates Section */}
      <section className="candidates-section">
        <h2>üë• Candidates</h2>

        {candidates.length === 0 ? (
          <div className="no-candidates">
            <h3>No candidates available</h3>
            <p>Please wait while candidates are being loaded...</p>
          </div>
        ) : (
          <div className="candidates-grid">
            {candidates.map((candidate, index) => (
              <div key={candidate.id} className="candidate-card">
                <div className="candidate-header">
                  <h3>üë§ {candidate.name}</h3>
                  <div className="vote-count">
                    <span className="votes">{candidate.voteCount}</span>
                    <span className="votes-label">votes</span>
                  </div>
                </div>

                <div className="vote-percentage">
                  {totalVotes > 0 ? Math.round((candidate.voteCount / totalVotes) * 100) : 0}%
                </div>

                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{ 
                      width: `${totalVotes > 0 ? (candidate.voteCount / totalVotes) * 100 : 0}%` 
                    }}
                  ></div>
                </div>

                {isRegistered && !hasVoted && votingActive && (
                  <button 
                    onClick={() => vote(candidate.id)}
                    disabled={loading}
                    className="btn btn-vote"
                  >
                    üó≥Ô∏è Vote for {candidate.name}
                  </button>
                )}

                {hasVoted && (
                  <div className="voted-message">
                    ‚úÖ Thank you for voting!
                  </div>
                )}

                {!votingActive && (
                  <div className="inactive-message">
                    ‚è∏Ô∏è Voting is currently paused
                  </div>
                )}

                {!isRegistered && !isOwner && (
                  <div className="register-message">
                    üìù Register to vote
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </section>

      {/* User Status */}
      <section className="status-section">
        <h2>üìä Your Voting Status</h2>
        <div className="status-grid">
          <div className="status-card">
            <h4>üìù Registration</h4>
            <p className={isRegistered ? 'status-yes' : 'status-no'}>
              {isRegistered ? '‚úÖ Registered' : '‚ùå Not Registered'}
            </p>
          </div>
          <div className="status-card">
            <h4>üó≥Ô∏è Voting</h4>
            <p className={hasVoted ? 'status-yes' : 'status-no'}>
              {hasVoted ? '‚úÖ Voted' : '‚ùå Not Voted'}
            </p>
          </div>
          <div className="status-card">
            <h4>‚ö° System</h4>
            <p className={votingActive ? 'status-yes' : 'status-no'}>
              {votingActive ? 'üü¢ Active' : 'üî¥ Inactive'}
            </p>
          </div>
        </div>
      </section>

      <footer className="footer">
        <p>üîó Connected to Ganache Local Network (Chain ID: 1337)</p>
        <p>üí° This is a demonstration of blockchain-based voting</p>
      </footer>
    </div>
  );
}

export default App;
