import React, { useState, useEffect } from 'react';

const VoterPortal = ({ contract, account, onDisconnect }) => {
  const [activeTab, setActiveTab] = useState('active-sessions');
  const [activeSessions, setActiveSessions] = useState([]);
  const [completedSessions, setCompletedSessions] = useState([]);
  const [selectedSession, setSelectedSession] = useState(null);
  const [sessionCandidates, setSessionCandidates] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [userVotes, setUserVotes] = useState({});

  useEffect(() => {
    loadData();
    setupEventListeners();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      await Promise.all([
        loadActiveSessions(),
        loadCompletedSessions(),
        loadUserVotes()
      ]);
    } catch (error) {
      setError(`Error loading data: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const loadActiveSessions = async () => {
    try {
      const activeSessionsData = await contract.getActiveSessions();
      const sessionsWithDetails = await Promise.all(
        activeSessionsData.map(async (session) => {
          const hasVoted = await contract.hasUserVoted(session.id, account);
          const totalVotes = session.totalVotes.toString();

          return {
            ...session,
            id: session.id.toString(),
            startTime: new Date(Number(session.startTime) * 1000),
            endTime: new Date(Number(session.endTime) * 1000),
            totalVotes,
            hasVoted
          };
        })
      );

      setActiveSessions(sessionsWithDetails);
    } catch (error) {
      console.error('Error loading active sessions:', error);
    }
  };

  const loadCompletedSessions = async () => {
    try {
      const completedSessionsData = await contract.getCompletedSessions(10);
      const sessionsWithDetails = await Promise.all(
        completedSessionsData.map(async (session) => {
          const [winnerName, winnerVotes] = await contract.getSessionWinner(session.id);
          const hasVoted = await contract.hasUserVoted(session.id, account);
          const candidates = await contract.getSessionCandidates(session.id);

          return {
            ...session,
            id: session.id.toString(),
            startTime: new Date(Number(session.startTime) * 1000),
            endTime: new Date(Number(session.endTime) * 1000),
            totalVotes: session.totalVotes.toString(),
            winner: { name: winnerName, votes: winnerVotes.toString() },
            hasVoted,
            candidates: candidates.filter(c => c.isActive).map(c => ({
              ...c,
              id: c.id.toString(),
              voteCount: c.voteCount.toString()
            }))
          };
        })
      );

      setCompletedSessions(sessionsWithDetails);
    } catch (error) {
      console.error('Error loading completed sessions:', error);
    }
  };

  const loadUserVotes = async () => {
    try {
      const votes = {};
      const allSessions = [...activeSessions, ...completedSessions];

      for (const session of allSessions) {
        const hasVoted = await contract.hasUserVoted(session.id, account);
        if (hasVoted) {
          const candidateId = await contract.getUserVoteInSession(session.id, account);
          votes[session.id] = candidateId.toString();
        }
      }

      setUserVotes(votes);
    } catch (error) {
      console.error('Error loading user votes:', error);
    }
  };

  const setupEventListeners = () => {
    contract.on("VoteCast", (sessionId, candidateId, voter) => {
      if (voter.toLowerCase() === account.toLowerCase()) {
        setUserVotes(prev => ({
          ...prev,
          [sessionId.toString()]: candidateId.toString()
        }));
      }
      loadData(); // Refresh all data when any vote is cast
    });

    contract.on("SessionStarted", () => {
      loadActiveSessions();
    });

    contract.on("SessionStopped", () => {
      loadData();
    });
  };

  const selectSession = async (session) => {
    try {
      setLoading(true);
      const candidates = await contract.getSessionCandidates(session.id);
      setSessionCandidates(candidates.filter(c => c.isActive).map(c => ({
        ...c,
        id: c.id.toString(),
        voteCount: c.voteCount.toString()
      })));
      setSelectedSession(session);
      setActiveTab('vote');
    } catch (error) {
      setError(`Error loading session details: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const vote = async (candidateId) => {
    try {
      setLoading(true);
      const transaction = await contract.vote(selectedSession.id, candidateId);
      await transaction.wait();

      // Update local state immediately
      setUserVotes(prev => ({
        ...prev,
        [selectedSession.id]: candidateId
      }));

      // Update session to show user has voted
      setSelectedSession(prev => ({
        ...prev,
        hasVoted: true,
        totalVotes: (parseInt(prev.totalVotes) + 1).toString()
      }));

      // Update candidates vote count locally for immediate feedback
      setSessionCandidates(prev => 
        prev.map(candidate => 
          candidate.id === candidateId
            ? { ...candidate, voteCount: (parseInt(candidate.voteCount) + 1).toString() }
            : candidate
        )
      );

      alert('✅ Vote submitted successfully!');

    } catch (error) {
      setError(`Error voting: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const formatDateTime = (date) => {
    return date.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatTimeRemaining = (endTime) => {
    const now = new Date();
    const diff = endTime - now;

    if (diff <= 0) return 'Ended';

    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));

    if (hours > 24) {
      const days = Math.floor(hours / 24);
      return `${days}d ${hours % 24}h remaining`;
    } else if (hours > 0) {
      return `${hours}h ${minutes}m remaining`;
    } else {
      return `${minutes}m remaining`;
    }
  };

  return (
    <div className="voter-portal">
      <header className="portal-header">
        <div className="header-content">
          <h1>🗳️ Voter Portal</h1>
          <div className="header-info">
            <span>Connected: {account.slice(0, 6)}...{account.slice(-4)}</span>
            <button onClick={onDisconnect} className="btn btn-disconnect">
              Disconnect
            </button>
          </div>
        </div>

        <nav className="portal-nav">
          <button 
            className={`nav-btn ${activeTab === 'active-sessions' ? 'active' : ''}`}
            onClick={() => setActiveTab('active-sessions')}
          >
            🔴 Active Sessions
          </button>
          <button 
            className={`nav-btn ${activeTab === 'vote' ? 'active' : ''}`}
            onClick={() => setActiveTab('vote')}
            disabled={!selectedSession}
          >
            🗳️ Vote
          </button>
          <button 
            className={`nav-btn ${activeTab === 'completed' ? 'active' : ''}`}
            onClick={() => setActiveTab('completed')}
          >
            📊 Results
          </button>
        </nav>
      </header>

      <main className="portal-content">
        {error && (
          <div className="error-banner">
            <span>⚠️ {error}</span>
            <button onClick={() => setError('')}>✕</button>
          </div>
        )}

        {activeTab === 'active-sessions' && (
          <div className="active-sessions">
            <h2>🔴 Active Voting Sessions</h2>

            {activeSessions.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">🗳️</div>
                <h3>No Active Sessions</h3>
                <p>There are currently no active voting sessions. Check back later!</p>
              </div>
            ) : (
              <div className="sessions-grid">
                {activeSessions.map(session => (
                  <div 
                    key={session.id} 
                    className={`session-card ${session.hasVoted ? 'voted' : 'not-voted'}`}
                  >
                    <div className="session-header">
                      <h3>{session.name}</h3>
                      <div className="session-badges">
                        <span className="status-badge active">🟢 Live</span>
                        {session.hasVoted && (
                          <span className="voted-badge">✅ Voted</span>
                        )}
                      </div>
                    </div>

                    <p className="session-description">{session.description}</p>

                    <div className="session-stats">
                      <div className="stat">
                        <span className="stat-icon">📊</span>
                        <span>{session.totalVotes} votes cast</span>
                      </div>
                      <div className="stat">
                        <span className="stat-icon">⏰</span>
                        <span>{formatTimeRemaining(session.endTime)}</span>
                      </div>
                    </div>

                    <div className="session-timing">
                      <small>Ends: {formatDateTime(session.endTime)}</small>
                    </div>

                    <div className="session-actions">
                      {session.hasVoted ? (
                        <button 
                          onClick={() => selectSession(session)}
                          className="btn btn-secondary"
                        >
                          👁️ View Details
                        </button>
                      ) : (
                        <button 
                          onClick={() => selectSession(session)}
                          className="btn btn-primary"
                        >
                          🗳️ Vote Now
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {activeTab === 'vote' && selectedSession && (
          <div className="vote-session">
            <div className="vote-header">
              <button 
                onClick={() => setActiveTab('active-sessions')}
                className="btn btn-back"
              >
                ← Back to Sessions
              </button>
              <div className="session-info">
                <h2>{selectedSession.name}</h2>
                <p>{selectedSession.description}</p>
                <div className="session-meta">
                  <span>📊 {selectedSession.totalVotes} votes cast</span>
                  <span>⏰ {formatTimeRemaining(selectedSession.endTime)}</span>
                </div>
              </div>
            </div>

            {selectedSession.hasVoted || userVotes[selectedSession.id] ? (
              <div className="already-voted">
                <div className="voted-message">
                  <span className="voted-icon">✅</span>
                  <h3>Thank you for voting!</h3>
                  <p>You have already cast your vote in this session.</p>
                </div>

                <div className="live-results">
                  <h3>📊 Live Results</h3>
                  <div className="candidates-results">
                    {sessionCandidates
                      .sort((a, b) => parseInt(b.voteCount) - parseInt(a.voteCount))
                      .map((candidate, index) => {
                        const isUserVote = userVotes[selectedSession.id] === candidate.id;
                        const totalVotes = parseInt(selectedSession.totalVotes);
                        const candidateVotes = parseInt(candidate.voteCount);
                        const percentage = totalVotes > 0 ? Math.round((candidateVotes / totalVotes) * 100) : 0;

                        return (
                          <div 
                            key={candidate.id} 
                            className={`candidate-result ${isUserVote ? 'user-vote' : ''}`}
                          >
                            <div className="candidate-info">
                              <span className="position">
                                {index === 0 ? '🥇' : index === 1 ? '🥈' : index === 2 ? '🥉' : `${index + 1}.`}
                              </span>
                              <span className="candidate-name">
                                {candidate.name}
                                {isUserVote && <span className="your-vote">👈 Your vote</span>}
                              </span>
                            </div>
                            <div className="vote-info">
                              <span className="vote-count">{candidate.voteCount} votes</span>
                              <span className="vote-percentage">{percentage}%</span>
                            </div>
                            <div className="vote-bar">
                              <div 
                                className={`vote-fill ${isUserVote ? 'user-vote-fill' : ''}`}
                                style={{ width: `${percentage}%` }}
                              />
                            </div>
                          </div>
                        );
                      })}
                  </div>
                </div>
              </div>
            ) : (
              <div className="voting-interface">
                <div className="voting-instructions">
                  <h3>🗳️ Cast Your Vote</h3>
                  <p>Select your preferred candidate below. You can only vote once in this session.</p>
                </div>

                <div className="candidates-voting">
                  {sessionCandidates.map(candidate => (
                    <div key={candidate.id} className="candidate-vote-card">
                      <div className="candidate-info">
                        <h4>{candidate.name}</h4>
                        <div className="candidate-stats">
                          <span>Current votes: {candidate.voteCount}</span>
                        </div>
                      </div>
                      <button
                        onClick={() => vote(candidate.id)}
                        disabled={loading}
                        className="btn btn-vote"
                      >
                        {loading ? 'Voting...' : `Vote for ${candidate.name}`}
                      </button>
                    </div>
                  ))}
                </div>

                <div className="voting-footer">
                  <p className="voting-notice">
                    ⚠️ <strong>Important:</strong> Your vote is permanent and cannot be changed once submitted.
                  </p>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'completed' && (
          <div className="completed-sessions">
            <h2>📊 Recent Election Results</h2>
            <p className="section-subtitle">View results from the last 10 completed voting sessions</p>

            {completedSessions.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">📊</div>
                <h3>No Completed Sessions</h3>
                <p>No voting sessions have been completed yet.</p>
              </div>
            ) : (
              <div className="results-list">
                {completedSessions.map(session => {
                  const userVoted = userVotes[session.id] || session.hasVoted;
                  const userVoteCandidate = userVoted ? 
                    session.candidates.find(c => c.id === userVotes[session.id]) : null;

                  return (
                    <div key={session.id} className="result-card">
                      <div className="result-header">
                        <div className="result-title">
                          <h3>{session.name}</h3>
                          <div className="result-badges">
                            <span className="status-badge completed">✅ Completed</span>
                            {userVoted && (
                              <span className="participated-badge">🗳️ You participated</span>
                            )}
                          </div>
                        </div>
                        <div className="result-meta">
                          <p>{session.description}</p>
                          <div className="result-stats">
                            <span>📊 {session.totalVotes} total votes</span>
                            <span>🕒 {formatDateTime(session.endTime)}</span>
                            <span>🏆 Winner: {session.winner.name} ({session.winner.votes} votes)</span>
                          </div>
                        </div>
                      </div>

                      {userVoted && userVoteCandidate && (
                        <div className="user-vote-info">
                          <p>
                            <span className="vote-icon">👤</span>
                            You voted for: <strong>{userVoteCandidate.name}</strong> 
                            ({userVoteCandidate.voteCount} votes total)
                          </p>
                        </div>
                      )}

                      <div className="detailed-results">
                        <h4>Detailed Results</h4>
                        <div className="candidates-results">
                          {session.candidates
                            .sort((a, b) => parseInt(b.voteCount) - parseInt(a.voteCount))
                            .map((candidate, index) => {
                              const isUserVote = userVotes[session.id] === candidate.id;
                              const totalVotes = parseInt(session.totalVotes);
                              const candidateVotes = parseInt(candidate.voteCount);
                              const percentage = totalVotes > 0 ? Math.round((candidateVotes / totalVotes) * 100) : 0;

                              return (
                                <div 
                                  key={candidate.id} 
                                  className={`candidate-result ${isUserVote ? 'user-vote' : ''} ${index === 0 ? 'winner' : ''}`}
                                >
                                  <div className="candidate-info">
                                    <span className="position">
                                      {index === 0 ? '🏆' : index === 1 ? '🥈' : index === 2 ? '🥉' : `${index + 1}.`}
                                    </span>
                                    <span className="candidate-name">
                                      {candidate.name}
                                      {isUserVote && <span className="your-vote">👈 Your vote</span>}
                                      {index === 0 && <span className="winner-label">Winner</span>}
                                    </span>
                                  </div>
                                  <div className="vote-info">
                                    <span className="vote-count">{candidate.voteCount} votes</span>
                                    <span className="vote-percentage">{percentage}%</span>
                                  </div>
                                  <div className="vote-bar">
                                    <div 
                                      className={`vote-fill ${isUserVote ? 'user-vote-fill' : ''} ${index === 0 ? 'winner-fill' : ''}`}
                                      style={{ width: `${percentage}%` }}
                                    />
                                  </div>
                                </div>
                              );
                            })}
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        )}
      </main>

      {loading && (
        <div className="loading-overlay">
          <div className="loading-spinner"></div>
          <p>Processing...</p>
        </div>
      )}
    </div>
  );
};

export default VoterPortal;
