import React, { useState, useEffect } from 'react';
import ActiveSessions from './ActiveSessions';
import VotingResults from './VotingResults';

const VoterPortal = ({ contract, account, isRegistered, setIsRegistered, onLogout }) => {
  const [activeTab, setActiveTab] = useState('vote');
  const [activeSessions, setActiveSessions] = useState([]);
  const [allSessions, setAllSessions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [autoStarting, setAutoStarting] = useState(false);

  useEffect(() => {
    loadSessions();
  }, [contract]);

  const autoStartEligibleSessions = async (allSessionsData) => {
    if (!contract || autoStarting) return allSessionsData;

    try {
      const now = new Date();
      const sessionsToStart = allSessionsData.filter(session => {
        // Session should auto-start if:
        // 1. Current time >= start time
        // 2. Current time <= end time  
        // 3. Session is not active yet
        return now >= session.startTime && 
               now <= session.endTime && 
               !session.isActive;
      });

      if (sessionsToStart.length > 0) {
        console.log(`Auto-starting ${sessionsToStart.length} eligible sessions...`);
        setAutoStarting(true);

        for (const session of sessionsToStart) {
          try {
            console.log(`Auto-starting session: ${session.name}`);
            const transaction = await contract.startSession(session.id);
            await transaction.wait();
            console.log(`Successfully auto-started session: ${session.name}`);

            // Update the session in our local data
            session.isActive = true;
          } catch (error) {
            console.error(`Failed to auto-start session ${session.name}:`, error);
            // Continue with other sessions even if one fails
          }
        }
        setAutoStarting(false);
      }

      return allSessionsData;
    } catch (error) {
      console.error('Error in auto-start process:', error);
      setAutoStarting(false);
      return allSessionsData;
    }
  };

  const loadSessions = async () => {
    try {
      setLoading(true);
      const now = new Date();

      // Load all sessions first
      const allSessionIds = await contract.getAllSessions();
      let allSessionsData = [];

      for (const sessionId of allSessionIds) {
        const sessionDetails = await contract.getSession(sessionId);
        const sessionStartTime = new Date(Number(sessionDetails.startTime) * 1000);
        const sessionEndTime = new Date(Number(sessionDetails.endTime) * 1000);
        const isActive = await contract.isSessionActive(sessionId);

        allSessionsData.push({
          id: sessionId.toString(),
          name: sessionDetails.sessionName,
          startTime: sessionStartTime,
          endTime: sessionEndTime,
          candidateCount: Number(sessionDetails.candidateCount),
          totalVotes: Number(sessionDetails.totalVotes),
          isActive
        });
      }

      // Auto-start eligible sessions
      allSessionsData = await autoStartEligibleSessions(allSessionsData);

      // Now filter active sessions - only sessions that are truly active and within time range
      const activeSessionsData = [];

      for (const session of allSessionsData) {
        // ‚úÖ FIXED: Only include sessions that are active AND within valid time range
        if (session.isActive && now >= session.startTime && now <= session.endTime) {
          const hasVoted = await contract.hasVotedInSession(account, session.id);

          activeSessionsData.push({
            ...session,
            hasVoted
          });
        }
      }

      setActiveSessions(activeSessionsData);

      // Prepare all sessions for results - properly categorized
      const resultSessionsData = allSessionsData.map(session => {
        // ‚úÖ FIXED: Proper session categorization
        let sessionStatus = 'pending';
        if (now >= session.startTime && now <= session.endTime && session.isActive) {
          sessionStatus = 'active';
        } else if (now > session.endTime || (now > session.startTime && !session.isActive)) {
          sessionStatus = 'completed';
        }

        return {
          ...session,
          status: sessionStatus
        };
      });

      // Sort by date (newest first) and filter for results view
      const sortedSessions = resultSessionsData
        .filter(session => session.status === 'completed' || (session.status === 'active' && session.totalVotes > 0))
        .sort((a, b) => b.endTime - a.endTime)
        .slice(0, 10);

      setAllSessions(sortedSessions);

    } catch (error) {
      console.error('Error loading sessions:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRegistration = async () => {
    try {
      setLoading(true);
      const transaction = await contract.registerSelf();
      await transaction.wait();
      setIsRegistered(true);
      alert('‚úÖ Successfully registered to vote!');
    } catch (error) {
      console.error('Error registering:', error);
      alert('‚ùå Registration failed: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleVoteSuccess = () => {
    loadSessions(); // Reload to update voting status
  };

  return (
    <div className="voter-portal">
      <header className="portal-header">
        <div className="header-content">
          <h1>üó≥Ô∏è Voter Portal</h1>
          <div className="header-info">
            <span className="account-badge">
              üì± {account.slice(0, 6)}...{account.slice(-4)}
            </span>
            <div className="registration-status">
              {isRegistered ? (
                <span className="status-badge registered">‚úÖ Registered</span>
              ) : (
                <span className="status-badge unregistered">‚ùå Not Registered</span>
              )}
            </div>
            <button onClick={onLogout} className="btn btn-secondary btn-small">
              üö™ Logout
            </button>
          </div>
        </div>

        <nav className="portal-nav">
          <button 
            onClick={() => setActiveTab('vote')}
            className={`nav-btn ${activeTab === 'vote' ? 'active' : ''}`}
          >
            üó≥Ô∏è Vote in Sessions
            {activeSessions.length > 0 && (
              <span className="nav-badge">{activeSessions.length}</span>
            )}
          </button>
          <button 
            onClick={() => setActiveTab('results')}
            className={`nav-btn ${activeTab === 'results' ? 'active' : ''}`}
          >
            üìà View Results
          </button>
        </nav>
      </header>

      <main className="portal-content">
        {!isRegistered ? (
          <div className="registration-required">
            <div className="registration-card">
              <h2>üìù Registration Required</h2>
              <p>You need to register as a voter before you can participate in voting sessions.</p>

              <div className="registration-benefits">
                <h3>‚ú® After registration, you can:</h3>
                <ul>
                  <li>üó≥Ô∏è Vote in all active sessions</li>
                  <li>üìä View session results</li>
                  <li>üìà Track your voting history</li>
                  <li>üèÜ See winners of completed sessions</li>
                </ul>
              </div>

              <button 
                onClick={handleRegistration}
                disabled={loading || autoStarting}
                className="btn btn-primary btn-large"
              >
                {loading ? (
                  <>
                    <div className="btn-spinner"></div>
                    Registering...
                  </>
                ) : (
                  'üìù Register as Voter'
                )}
              </button>

              <div className="registration-note">
                <p>üîí Registration is free and secure on the blockchain</p>
                {autoStarting && (
                  <p className="auto-start-note">
                    ‚è∞ Sessions are automatically starting based on their scheduled time...
                  </p>
                )}
              </div>
            </div>
          </div>
        ) : (
          <>
            {activeTab === 'vote' && (
              <ActiveSessions 
                contract={contract}
                account={account}
                sessions={activeSessions}
                loading={loading || autoStarting}
                onVoteSuccess={handleVoteSuccess}
              />
            )}

            {activeTab === 'results' && (
              <VotingResults 
                contract={contract}
                sessions={allSessions}
                isVoterView={true}
              />
            )}
          </>
        )}
      </main>
    </div>
  );
};

export default VoterPortal;
