import React, { useState, useEffect } from 'react';
import './OwnerPortal.css';

const OwnerPortal = ({ contract, account, onClose }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(false);
  const [candidates, setCandidates] = useState([]);
  const [votingSessions, setVotingSessions] = useState([]);
  const [currentSession, setCurrentSession] = useState(null);
  const [votingActive, setVotingActive] = useState(false);

  // Form states
  const [candidateForm, setCandidateForm] = useState({
    name: '',
    description: '',
    imageUrl: '',
    isEditing: false,
    editId: null
  });

  const [sessionForm, setSessionForm] = useState({
    title: '',
    description: ''
  });

  const [schedulingForm, setSchedulingForm] = useState({
    startDate: '',
    startTime: '',
    endDate: '',
    endTime: '',
    duration: '1' // hours
  });

  useEffect(() => {
    if (contract) {
      loadOwnerData();
    }
  }, [contract]);

  const loadOwnerData = async () => {
    try {
      setLoading(true);
      await Promise.all([
        loadCandidates(),
        loadVotingSessions(),
        loadCurrentSession(),
        checkVotingStatus()
      ]);
    } catch (error) {
      console.error('Error loading owner data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadCandidates = async () => {
    try {
      const candidatesData = await contract.getAllCandidates();
      const candidatesList = candidatesData.map((candidate) => ({
        id: candidate.id.toString(),
        name: candidate.name,
        description: candidate.description,
        imageUrl: candidate.imageUrl,
        voteCount: candidate.voteCount.toString(),
        isActive: candidate.isActive
      }));
      setCandidates(candidatesList);
    } catch (error) {
      console.error('Error loading candidates:', error);
    }
  };

  const loadVotingSessions = async () => {
    try {
      const sessionsData = await contract.getAllVotingSessions();
      const sessionsList = sessionsData.map((session) => ({
        id: session.id.toString(),
        title: session.title,
        description: session.description,
        startTime: new Date(Number(session.startTime) * 1000),
        endTime: new Date(Number(session.endTime) * 1000),
        isActive: session.isActive,
        isCompleted: session.isCompleted,
        totalVotes: session.totalVotes.toString(),
        winnerName: session.winnerName,
        winnerVotes: session.winnerVotes.toString(),
        createdAt: new Date(Number(session.createdAt) * 1000)
      }));
      setVotingSessions(sessionsList);
    } catch (error) {
      console.error('Error loading voting sessions:', error);
    }
  };

  const loadCurrentSession = async () => {
    try {
      const sessionInfo = await contract.getCurrentSessionInfo();
      setCurrentSession({
        id: sessionInfo.sessionId.toString(),
        title: sessionInfo.title,
        description: sessionInfo.description,
        startTime: new Date(Number(sessionInfo.startTime) * 1000),
        endTime: new Date(Number(sessionInfo.endTime) * 1000),
        isActive: sessionInfo.isActive,
        totalVotes: sessionInfo.totalVotesCount.toString()
      });
    } catch (error) {
      console.error('Error loading current session:', error);
    }
  };

  const checkVotingStatus = async () => {
    try {
      const status = await contract.votingActive();
      setVotingActive(status);
    } catch (error) {
      console.error('Error checking voting status:', error);
    }
  };

  // Candidate Management Functions
  const handleAddCandidate = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const transaction = await contract.addCandidate(
        candidateForm.name,
        candidateForm.description,
        candidateForm.imageUrl
      );
      await transaction.wait();

      setCandidateForm({ name: '', description: '', imageUrl: '', isEditing: false, editId: null });
      await loadCandidates();
      alert('✅ Candidate added successfully!');
    } catch (error) {
      console.error('Error adding candidate:', error);
      alert('❌ Error adding candidate: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateCandidate = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const transaction = await contract.updateCandidate(
        candidateForm.editId,
        candidateForm.name,
        candidateForm.description,
        candidateForm.imageUrl
      );
      await transaction.wait();

      setCandidateForm({ name: '', description: '', imageUrl: '', isEditing: false, editId: null });
      await loadCandidates();
      alert('✅ Candidate updated successfully!');
    } catch (error) {
      console.error('Error updating candidate:', error);
      alert('❌ Error updating candidate: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEditCandidate = (candidate) => {
    setCandidateForm({
      name: candidate.name,
      description: candidate.description,
      imageUrl: candidate.imageUrl,
      isEditing: true,
      editId: candidate.id
    });
    setActiveTab('candidates');
  };

  const handleToggleCandidateVisibility = async (candidateId) => {
    try {
      setLoading(true);
      const transaction = await contract.toggleCandidateVisibility(candidateId);
      await transaction.wait();
      await loadCandidates();
      alert('✅ Candidate visibility updated!');
    } catch (error) {
      console.error('Error toggling candidate visibility:', error);
      alert('❌ Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveCandidate = async (candidateId) => {
    if (!window.confirm('Are you sure you want to remove this candidate? This action cannot be undone.')) {
      return;
    }

    try {
      setLoading(true);
      const transaction = await contract.removeCandidate(candidateId);
      await transaction.wait();
      await loadCandidates();
      alert('✅ Candidate removed successfully!');
    } catch (error) {
      console.error('Error removing candidate:', error);
      alert('❌ Error removing candidate: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Voting Session Management
  const handleCreateNewSession = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const transaction = await contract.createNewVotingSession(
        sessionForm.title,
        sessionForm.description
      );
      await transaction.wait();

      setSessionForm({ title: '', description: '' });
      await loadOwnerData();
      alert('✅ New voting session created successfully!');
    } catch (error) {
      console.error('Error creating new session:', error);
      alert('❌ Error creating session: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Voting Control Functions
  const handleStartVoting = async () => {
    try {
      setLoading(true);
      const transaction = await contract.startVoting();
      await transaction.wait();
      await loadOwnerData();
      alert('✅ Voting started successfully!');
    } catch (error) {
      console.error('Error starting voting:', error);
      alert('❌ Error starting voting: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleStartVotingWithDuration = async () => {
    try {
      const durationInSeconds = parseInt(schedulingForm.duration) * 3600; // Convert hours to seconds
      setLoading(true);
      const transaction = await contract.startVotingWithDuration(durationInSeconds);
      await transaction.wait();
      await loadOwnerData();
      alert(`✅ Voting started for ${schedulingForm.duration} hours!`);
    } catch (error) {
      console.error('Error starting voting with duration:', error);
      alert('❌ Error: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleScheduleVoting = async () => {
    try {
      const startTimestamp = Math.floor(new Date(`${schedulingForm.startDate}T${schedulingForm.startTime}`).getTime() / 1000);
      const endTimestamp = Math.floor(new Date(`${schedulingForm.endDate}T${schedulingForm.endTime}`).getTime() / 1000);

      setLoading(true);
      const transaction = await contract.scheduleVoting(startTimestamp, endTimestamp);
      await transaction.wait();
      await loadOwnerData();
      alert('✅ Voting scheduled successfully!');
    } catch (error) {
      console.error('Error scheduling voting:', error);
      alert('❌ Error scheduling voting: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleStopVoting = async () => {
    if (!window.confirm('Are you sure you want to stop the current voting session?')) {
      return;
    }

    try {
      setLoading(true);
      const transaction = await contract.stopVoting();
      await transaction.wait();
      await loadOwnerData();
      alert('✅ Voting stopped successfully!');
    } catch (error) {
      console.error('Error stopping voting:', error);
      alert('❌ Error stopping voting: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCompleteSession = async () => {
    if (!window.confirm('Are you sure you want to complete the current session? This will finalize all results.')) {
      return;
    }

    try {
      setLoading(true);
      const transaction = await contract.completeCurrentSession();
      await transaction.wait();
      await loadOwnerData();
      alert('✅ Session completed successfully!');
    } catch (error) {
      console.error('Error completing session:', error);
      alert('❌ Error completing session: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const renderDashboard = () => (
    <div className="dashboard-content">
      <div className="dashboard-stats">
        <div className="stat-card">
          <h3>Current Session</h3>
          <p className="stat-number">{currentSession?.title || 'No active session'}</p>
          <p className="stat-label">Status: {votingActive ? '🟢 Active' : '🔴 Inactive'}</p>
        </div>
        <div className="stat-card">
          <h3>Total Candidates</h3>
          <p className="stat-number">{candidates.length}</p>
          <p className="stat-label">Active: {candidates.filter(c => c.isActive).length}</p>
        </div>
        <div className="stat-card">
          <h3>Total Votes</h3>
          <p className="stat-number">{currentSession?.totalVotes || '0'}</p>
          <p className="stat-label">Current session</p>
        </div>
        <div className="stat-card">
          <h3>Voting Sessions</h3>
          <p className="stat-number">{votingSessions.length}</p>
          <p className="stat-label">Completed: {votingSessions.filter(s => s.isCompleted).length}</p>
        </div>
      </div>

      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          {!votingActive ? (
            <button onClick={handleStartVoting} className="btn btn-success" disabled={loading}>
              ▶️ Start Voting
            </button>
          ) : (
            <button onClick={handleStopVoting} className="btn btn-danger" disabled={loading}>
              ⏹️ Stop Voting
            </button>
          )}
          <button onClick={() => setActiveTab('candidates')} className="btn btn-primary">
            👥 Manage Candidates
          </button>
          <button onClick={() => setActiveTab('sessions')} className="btn btn-info">
            📊 View Sessions
          </button>
          <button onClick={handleCompleteSession} className="btn btn-warning" disabled={loading}>
            ✅ Complete Session
          </button>
        </div>
      </div>
    </div>
  );

  const renderCandidateManagement = () => (
    <div className="candidate-management">
      <div className="candidate-form-section">
        <h3>{candidateForm.isEditing ? 'Edit Candidate' : 'Add New Candidate'}</h3>
        <form onSubmit={candidateForm.isEditing ? handleUpdateCandidate : handleAddCandidate}>
          <div className="form-group">
            <label>Candidate Name *</label>
            <input
              type="text"
              value={candidateForm.name}
              onChange={(e) => setCandidateForm({...candidateForm, name: e.target.value})}
              required
              placeholder="Enter candidate name"
            />
          </div>
          <div className="form-group">
            <label>Description</label>
            <textarea
              value={candidateForm.description}
              onChange={(e) => setCandidateForm({...candidateForm, description: e.target.value})}
              placeholder="Enter candidate description"
              rows="3"
            />
          </div>
          <div className="form-group">
            <label>Image URL (optional)</label>
            <input
              type="url"
              value={candidateForm.imageUrl}
              onChange={(e) => setCandidateForm({...candidateForm, imageUrl: e.target.value})}
              placeholder="https://example.com/image.jpg"
            />
          </div>
          <div className="form-buttons">
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {candidateForm.isEditing ? '💾 Update Candidate' : '➕ Add Candidate'}
            </button>
            {candidateForm.isEditing && (
              <button 
                type="button" 
                onClick={() => setCandidateForm({ name: '', description: '', imageUrl: '', isEditing: false, editId: null })}
                className="btn btn-secondary"
              >
                ❌ Cancel
              </button>
            )}
          </div>
        </form>
      </div>

      <div className="candidates-list-section">
        <h3>Manage Candidates ({candidates.length})</h3>
        {candidates.length === 0 ? (
          <p className="no-data">No candidates found.</p>
        ) : (
          <div className="candidates-table">
            {candidates.map((candidate) => (
              <div key={candidate.id} className={`candidate-row ${!candidate.isActive ? 'inactive' : ''}`}>
                <div className="candidate-info">
                  <div className="candidate-header">
                    <h4>{candidate.name}</h4>
                    <span className={`status-badge ${candidate.isActive ? 'active' : 'inactive'}`}>
                      {candidate.isActive ? '👁️ Visible' : '👁️‍🗨️ Hidden'}
                    </span>
                  </div>
                  <p className="candidate-description">{candidate.description || 'No description'}</p>
                  <div className="candidate-stats">
                    <span>📊 Votes: {candidate.voteCount}</span>
                    {candidate.imageUrl && <span>🖼️ Has Image</span>}
                  </div>
                </div>
                <div className="candidate-actions">
                  <button
                    onClick={() => handleEditCandidate(candidate)}
                    className="btn btn-sm btn-info"
                    disabled={loading}
                  >
                    ✏️ Edit
                  </button>
                  <button
                    onClick={() => handleToggleCandidateVisibility(candidate.id)}
                    className={`btn btn-sm ${candidate.isActive ? 'btn-warning' : 'btn-success'}`}
                    disabled={loading}
                  >
                    {candidate.isActive ? '👁️‍🗨️ Hide' : '👁️ Show'}
                  </button>
                  <button
                    onClick={() => handleRemoveCandidate(candidate.id)}
                    className="btn btn-sm btn-danger"
                    disabled={loading || votingActive}
                  >
                    🗑️ Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

  const renderVotingControl = () => (
    <div className="voting-control">
      <div className="current-session-info">
        <h3>Current Voting Session</h3>
        {currentSession ? (
          <div className="session-card current">
            <div className="session-header">
              <h4>{currentSession.title}</h4>
              <span className={`status-badge ${votingActive ? 'active' : 'inactive'}`}>
                {votingActive ? '🟢 Active' : '🔴 Inactive'}
              </span>
            </div>
            <p>{currentSession.description}</p>
            <div className="session-stats">
              <div className="stat">
                <span className="label">Total Votes:</span>
                <span className="value">{currentSession.totalVotes}</span>
              </div>
              {votingActive && (
                <>
                  <div className="stat">
                    <span className="label">Started:</span>
                    <span className="value">{currentSession.startTime.toLocaleString()}</span>
                  </div>
                  <div className="stat">
                    <span className="label">Ends:</span>
                    <span className="value">{currentSession.endTime.toLocaleString()}</span>
                  </div>
                </>
              )}
            </div>
          </div>
        ) : (
          <p className="no-data">No active session</p>
        )}
      </div>

      <div className="voting-controls">
        <h3>Voting Controls</h3>

        <div className="control-section">
          <h4>Quick Start/Stop</h4>
          <div className="control-buttons">
            {!votingActive ? (
              <>
                <button onClick={handleStartVoting} className="btn btn-success" disabled={loading}>
                  ▶️ Start Voting Now
                </button>
                <div className="duration-control">
                  <label>Duration (hours):</label>
                  <input
                    type="number"
                    min="1"
                    max="168"
                    value={schedulingForm.duration}
                    onChange={(e) => setSchedulingForm({...schedulingForm, duration: e.target.value})}
                  />
                  <button onClick={handleStartVotingWithDuration} className="btn btn-primary" disabled={loading}>
                    ⏱️ Start with Duration
                  </button>
                </div>
              </>
            ) : (
              <button onClick={handleStopVoting} className="btn btn-danger" disabled={loading}>
                ⏹️ Stop Voting
              </button>
            )}
          </div>
        </div>

        <div className="control-section">
          <h4>Schedule Voting</h4>
          <div className="scheduling-form">
            <div className="form-row">
              <div className="form-group">
                <label>Start Date</label>
                <input
                  type="date"
                  value={schedulingForm.startDate}
                  onChange={(e) => setSchedulingForm({...schedulingForm, startDate: e.target.value})}
                  min={new Date().toISOString().split('T')[0]}
                />
              </div>
              <div className="form-group">
                <label>Start Time</label>
                <input
                  type="time"
                  value={schedulingForm.startTime}
                  onChange={(e) => setSchedulingForm({...schedulingForm, startTime: e.target.value})}
                />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group">
                <label>End Date</label>
                <input
                  type="date"
                  value={schedulingForm.endDate}
                  onChange={(e) => setSchedulingForm({...schedulingForm, endDate: e.target.value})}
                  min={schedulingForm.startDate || new Date().toISOString().split('T')[0]}
                />
              </div>
              <div className="form-group">
                <label>End Time</label>
                <input
                  type="time"
                  value={schedulingForm.endTime}
                  onChange={(e) => setSchedulingForm({...schedulingForm, endTime: e.target.value})}
                />
              </div>
            </div>
            <button onClick={handleScheduleVoting} className="btn btn-info" disabled={loading}>
              📅 Schedule Voting
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  const renderSessionManagement = () => (
    <div className="session-management">
      <div className="new-session-form">
        <h3>Create New Voting Session</h3>
        <form onSubmit={handleCreateNewSession}>
          <div className="form-group">
            <label>Session Title *</label>
            <input
              type="text"
              value={sessionForm.title}
              onChange={(e) => setSessionForm({...sessionForm, title: e.target.value})}
              required
              placeholder="e.g., Student Council Election 2024"
            />
          </div>
          <div className="form-group">
            <label>Description</label>
            <textarea
              value={sessionForm.description}
              onChange={(e) => setSessionForm({...sessionForm, description: e.target.value})}
              placeholder="Describe the purpose of this voting session"
              rows="3"
            />
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            🆕 Create New Session
          </button>
        </form>
      </div>

      <div className="sessions-history">
        <h3>Voting Sessions History</h3>
        {votingSessions.length === 0 ? (
          <p className="no-data">No voting sessions found.</p>
        ) : (
          <div className="sessions-list">
            {votingSessions
              .sort((a, b) => b.createdAt - a.createdAt)
              .map((session) => (
                <div key={session.id} className={`session-card ${session.isActive ? 'active' : session.isCompleted ? 'completed' : 'scheduled'}`}>
                  <div className="session-header">
                    <h4>{session.title}</h4>
                    <div className="session-badges">
                      {session.isActive && <span className="badge active">🟢 Active</span>}
                      {session.isCompleted && <span className="badge completed">✅ Completed</span>}
                      {!session.isActive && !session.isCompleted && <span className="badge scheduled">📅 Scheduled</span>}
                    </div>
                  </div>

                  <p className="session-description">{session.description}</p>

                  <div className="session-stats">
                    <div className="stat">
                      <span className="label">Created:</span>
                      <span className="value">{session.createdAt.toLocaleDateString()}</span>
                    </div>
                    <div className="stat">
                      <span className="label">Total Votes:</span>
                      <span className="value">{session.totalVotes}</span>
                    </div>
                    {session.isCompleted && (
                      <>
                        <div className="stat">
                          <span className="label">Winner:</span>
                          <span className="value winner">{session.winnerName}</span>
                        </div>
                        <div className="stat">
                          <span className="label">Winning Votes:</span>
                          <span className="value">{session.winnerVotes}</span>
                        </div>
                      </>
                    )}
                  </div>

                  {(session.startTime.getTime() > 0) && (
                    <div className="session-timing">
                      <div className="timing-info">
                        <span>📅 {session.startTime.toLocaleString()}</span>
                        <span>→</span>
                        <span>📅 {session.endTime.toLocaleString()}</span>
                      </div>
                    </div>
                  )}
                </div>
              ))}
          </div>
        )}
      </div>
    </div>
  );

  const renderAnalytics = () => (
    <div className="analytics">
      <h3>Voting Analytics</h3>

      <div className="analytics-summary">
        <div className="summary-cards">
          <div className="summary-card">
            <h4>Total Sessions</h4>
            <p className="big-number">{votingSessions.length}</p>
          </div>
          <div className="summary-card">
            <h4>Completed Sessions</h4>
            <p className="big-number">{votingSessions.filter(s => s.isCompleted).length}</p>
          </div>
          <div className="summary-card">
            <h4>Total Votes Cast</h4>
            <p className="big-number">{votingSessions.reduce((total, session) => total + parseInt(session.totalVotes), 0)}</p>
          </div>
          <div className="summary-card">
            <h4>Active Candidates</h4>
            <p className="big-number">{candidates.filter(c => c.isActive).length}</p>
          </div>
        </div>
      </div>

      <div className="current-results">
        <h4>Current Session Results</h4>
        {candidates.length > 0 ? (
          <div className="results-chart">
            {candidates
              .filter(c => c.isActive)
              .sort((a, b) => parseInt(b.voteCount) - parseInt(a.voteCount))
              .map((candidate, index) => {
                const totalVotes = candidates.reduce((sum, c) => sum + parseInt(c.voteCount), 0);
                const percentage = totalVotes > 0 ? Math.round((parseInt(candidate.voteCount) / totalVotes) * 100) : 0;

                return (
                  <div key={candidate.id} className="result-bar">
                    <div className="candidate-info">
                      <span className="position">#{index + 1}</span>
                      <span className="name">{candidate.name}</span>
                      <span className="votes">{candidate.voteCount} votes ({percentage}%)</span>
                    </div>
                    <div className="progress-bar">
                      <div 
                        className="progress-fill" 
                        style={{ width: `${percentage}%` }}
                      ></div>
                    </div>
                  </div>
                );
              })}
          </div>
        ) : (
          <p className="no-data">No candidates available</p>
        )}
      </div>
    </div>
  );

  return (
    <div className="owner-portal-overlay">
      <div className="owner-portal">
        <div className="portal-header">
          <h2>👑 Owner Portal</h2>
          <button onClick={onClose} className="close-button">✕</button>
        </div>

        <div className="portal-navigation">
          <button 
            className={`nav-tab ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            📊 Dashboard
          </button>
          <button 
            className={`nav-tab ${activeTab === 'candidates' ? 'active' : ''}`}
            onClick={() => setActiveTab('candidates')}
          >
            👥 Candidates
          </button>
          <button 
            className={`nav-tab ${activeTab === 'voting' ? 'active' : ''}`}
            onClick={() => setActiveTab('voting')}
          >
            🗳️ Voting Control
          </button>
          <button 
            className={`nav-tab ${activeTab === 'sessions' ? 'active' : ''}`}
            onClick={() => setActiveTab('sessions')}
          >
            📋 Sessions
          </button>
          <button 
            className={`nav-tab ${activeTab === 'analytics' ? 'active' : ''}`}
            onClick={() => setActiveTab('analytics')}
          >
            📈 Analytics
          </button>
        </div>

        <div className="portal-content">
          {loading && <div className="loading-overlay">Loading...</div>}

          {activeTab === 'dashboard' && renderDashboard()}
          {activeTab === 'candidates' && renderCandidateManagement()}
          {activeTab === 'voting' && renderVotingControl()}
          {activeTab === 'sessions' && renderSessionManagement()}
          {activeTab === 'analytics' && renderAnalytics()}
        </div>
      </div>
    </div>
  );
};

export default OwnerPortal;
