import React, { useState, useEffect } from 'react';
import CreateSession from './CreateSession';
import SessionManager from './SessionManager';
import VotingResults from './VotingResults';

const OwnerPortal = ({ contract, account, onLogout }) => {
  const [activeTab, setActiveTab] = useState('sessions');
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [autoStarting, setAutoStarting] = useState(false);

  useEffect(() => {
    loadSessions();
  }, [contract]);

  const autoStartEligibleSessions = async (allSessionsData) => {
    if (!contract || autoStarting) return allSessionsData;

    try {
      const now = new Date();
      const sessionsToStart = allSessionsData.filter(session => {
        // Session should auto-start if:
        // 1. Current time >= start time
        // 2. Current time <= end time  
        // 3. Session is not active yet
        return now >= session.startTime && 
               now <= session.endTime && 
               !session.isActive;
      });

      if (sessionsToStart.length > 0) {
        console.log(`Auto-starting ${sessionsToStart.length} eligible sessions...`);
        setAutoStarting(true);

        for (const session of sessionsToStart) {
          try {
            console.log(`Auto-starting session: ${session.name}`);
            const transaction = await contract.startSession(session.id);
            await transaction.wait();
            console.log(`Successfully auto-started session: ${session.name}`);

            // Update the session in our local data
            session.isActive = true;
          } catch (error) {
            console.error(`Failed to auto-start session ${session.name}:`, error);
            // Continue with other sessions even if one fails
          }
        }
        setAutoStarting(false);
      }

      return allSessionsData;
    } catch (error) {
      console.error('Error in auto-start process:', error);
      setAutoStarting(false);
      return allSessionsData;
    }
  };

  const loadSessions = async () => {
    try {
      setLoading(true);
      const sessionIds = await contract.getAllSessions();
      let sessionsData = [];

      for (const sessionId of sessionIds) {
        const sessionDetails = await contract.getSession(sessionId);
        const isActive = await contract.isSessionActive(sessionId);

        sessionsData.push({
          id: sessionId.toString(),
          name: sessionDetails.sessionName,
          startTime: new Date(Number(sessionDetails.startTime) * 1000),
          endTime: new Date(Number(sessionDetails.endTime) * 1000),
          isActive,
          candidateCount: Number(sessionDetails.candidateCount),
          totalVotes: Number(sessionDetails.totalVotes)
        });
      }

      // Auto-start eligible sessions
      sessionsData = await autoStartEligibleSessions(sessionsData);

      setSessions(sessionsData);
    } catch (error) {
      console.error('Error loading sessions:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSessionCreated = () => {
    loadSessions();
    setActiveTab('sessions');
  };

  const handleSessionUpdated = () => {
    loadSessions();
  };

  return (
    <div className="owner-portal">
      <header className="portal-header">
        <div className="header-content">
          <h1>üëë Owner Portal</h1>
          <div className="header-info">
            <span className="account-badge">
              üì± {account.slice(0, 6)}...{account.slice(-4)}
            </span>
            {autoStarting && (
              <span className="auto-start-indicator">
                ‚è∞ Auto-starting sessions...
              </span>
            )}
            <button onClick={onLogout} className="btn btn-secondary btn-small">
              üö™ Logout
            </button>
          </div>
        </div>

        <nav className="portal-nav">
          <button 
            onClick={() => setActiveTab('sessions')}
            className={`nav-btn ${activeTab === 'sessions' ? 'active' : ''}`}
          >
            üìä Manage Sessions
          </button>
          <button 
            onClick={() => setActiveTab('create')}
            className={`nav-btn ${activeTab === 'create' ? 'active' : ''}`}
          >
            ‚ûï Create Session
          </button>
          <button 
            onClick={() => setActiveTab('results')}
            className={`nav-btn ${activeTab === 'results' ? 'active' : ''}`}
          >
            üìà View Results
          </button>
        </nav>
      </header>

      <main className="portal-content">
        {activeTab === 'sessions' && (
          <SessionManager 
            contract={contract}
            sessions={sessions}
            loading={loading || autoStarting}
            onSessionUpdated={handleSessionUpdated}
          />
        )}

        {activeTab === 'create' && (
          <CreateSession 
            contract={contract}
            onSessionCreated={handleSessionCreated}
          />
        )}

        {activeTab === 'results' && (
          <VotingResults 
            contract={contract}
            sessions={sessions}
          />
        )}
      </main>
    </div>
  );
};

export default OwnerPortal;
