import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import './App.css';
import OwnerPortal from './components/OwnerPortal';

// Import your contract ABI and address
import VotingSystemData from './contracts/VotingSystemAdvanced.json';

function App() {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [contract, setContract] = useState(null);
  const [candidates, setCandidates] = useState([]);
  const [account, setAccount] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isOwner, setIsOwner] = useState(false);
  const [isRegistered, setIsRegistered] = useState(false);
  const [hasVoted, setHasVoted] = useState(false);
  const [votingActive, setVotingActive] = useState(true);
  const [winner, setWinner] = useState({ name: '', voteCount: 0 });
  const [totalVotes, setTotalVotes] = useState(0);
  const [currentSession, setCurrentSession] = useState(null);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [showOwnerPortal, setShowOwnerPortal] = useState(false);

  useEffect(() => {
    init();
  }, []);

  useEffect(() => {
    let interval;
    if (votingActive && timeRemaining > 0) {
      interval = setInterval(() => {
        updateTimeRemaining();
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [votingActive, timeRemaining]);

  const init = async () => {
    try {
      setLoading(true);
      setError('');

      // Check if MetaMask is installed
      if (typeof window.ethereum !== 'undefined') {
        // Request account access
        await window.ethereum.request({ method: 'eth_requestAccounts' });

        const provider = new ethers.BrowserProvider(window.ethereum);
        const signer = await provider.getSigner();
        const address = await signer.getAddress();

        setProvider(provider);
        setSigner(signer);
        setAccount(address);

        // Initialize contract
        const votingContract = new ethers.Contract(
          VotingSystemData.address,
          VotingSystemData.abi,
          signer
        );

        setContract(votingContract);

        // Check if user is owner
        const owner = await votingContract.owner();
        setIsOwner(address.toLowerCase() === owner.toLowerCase());

        // Load all data
        await loadAllData(votingContract, address);

        // Listen for events
        setupEventListeners(votingContract);

      } else {
        setError('Please install MetaMask to use this application');
      }
    } catch (error) {
      console.error('Initialization error:', error);
      setError(`Error initializing app: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const loadAllData = async (contractInstance, userAddress) => {
    try {
      // Load voting status and session info
      const votingStatus = await contractInstance.votingActive();
      setVotingActive(votingStatus);

      const sessionInfo = await contractInstance.getCurrentSessionInfo();
      const session = {
        id: sessionInfo.sessionId.toString(),
        title: sessionInfo.title,
        description: sessionInfo.description,
        startTime: new Date(Number(sessionInfo.startTime) * 1000),
        endTime: new Date(Number(sessionInfo.endTime) * 1000),
        isActive: sessionInfo.isActive,
        totalVotes: sessionInfo.totalVotesCount.toString()
      };
      setCurrentSession(session);
      setTotalVotes(session.totalVotes);

      // Check user registration and voting status
      const [registered, voted] = await contractInstance.getVoterInfo(userAddress);
      setIsRegistered(registered);
      setHasVoted(voted);

      // Load candidates, winner and time remaining
      await loadCandidates(contractInstance);
      await loadWinner(contractInstance);
      await updateTimeRemaining(contractInstance);

    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const setupEventListeners = (contractInstance) => {
    contractInstance.on("VoteCast", async (voter, candidateId, timestamp) => {
      console.log(`Vote cast by ${voter} for candidate ${candidateId}`);
      await loadCandidates(contractInstance);
      await loadWinner(contractInstance);
      await updateTotalVotes(contractInstance);

      // Update user status if it's the current user
      if (voter.toLowerCase() === account.toLowerCase()) {
        setHasVoted(true);
      }
    });

    contractInstance.on("VoterRegistered", (voter) => {
      console.log(`Voter registered: ${voter}`);
      if (voter.toLowerCase() === account.toLowerCase()) {
        setIsRegistered(true);
      }
    });

    contractInstance.on("VotingStarted", async (startTime, endTime) => {
      console.log(`Voting started: ${startTime} - ${endTime}`);
      setVotingActive(true);
      await loadAllData(contractInstance, account);
    });

    contractInstance.on("VotingStopped", async () => {
      console.log('Voting stopped');
      setVotingActive(false);
      await loadAllData(contractInstance, account);
    });

    contractInstance.on("NewVotingSessionCreated", async (sessionId, title) => {
      console.log(`New voting session created: ${sessionId} - ${title}`);
      await loadAllData(contractInstance, account);
    });
  };

  const updateTimeRemaining = async (contractInstance = contract) => {
    if (!contractInstance) return;

    try {
      const remaining = await contractInstance.getVotingTimeRemaining();
      setTimeRemaining(Number(remaining));
    } catch (error) {
      console.error('Error updating time remaining:', error);
    }
  };

  const updateTotalVotes = async (contractInstance) => {
    try {
      const sessionInfo = await contractInstance.getCurrentSessionInfo();
      setTotalVotes(sessionInfo.totalVotesCount.toString());
    } catch (error) {
      console.error('Error updating total votes:', error);
    }
  };

  const loadCandidates = async (contractInstance) => {
    try {
      const candidatesData = await contractInstance.getActiveCandidates();
      const candidatesList = candidatesData.map((candidate) => ({
        id: candidate.id.toString(),
        name: candidate.name,
        description: candidate.description,
        imageUrl: candidate.imageUrl,
        voteCount: candidate.voteCount.toString(),
        isActive: candidate.isActive
      }));

      setCandidates(candidatesList);

    } catch (error) {
      console.error('Error loading candidates:', error);
      setError(`Error loading candidates: ${error.message}`);
    }
  };

  const loadWinner = async (contractInstance) => {
    try {
      const [winnerName, winnerVoteCount] = await contractInstance.getWinner();
      setWinner({
        name: winnerName,
        voteCount: winnerVoteCount.toString()
      });
    } catch (error) {
      console.error('Error loading winner:', error);
    }
  };

  const registerSelf = async () => {
    try {
      setLoading(true);
      const transaction = await contract.registerSelf();
      await transaction.wait();
      setIsRegistered(true);
      alert('✅ Successfully registered to vote!');
    } catch (error) {
      console.error('Error registering:', error);
      setError(`Error registering: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const vote = async (candidateId) => {
    try {
      if (!contract) {
        setError('Contract not initialized');
        return;
      }

      setLoading(true);
      const transaction = await contract.vote(candidateId);
      await transaction.wait();

      setHasVoted(true);
      alert('✅ Vote submitted successfully!');
    } catch (error) {
      console.error('Error voting:', error);
      if (error.message.includes('You have already voted')) {
        setError('You have already voted!');
      } else if (error.message.includes('You are not registered to vote')) {
        setError('You are not registered to vote!');
      } else if (error.message.includes('Voting is not active')) {
        setError('Voting is not currently active!');
      } else if (error.message.includes('Voting has not started yet')) {
        setError('Voting has not started yet!');
      } else if (error.message.includes('Voting has ended')) {
        setError('Voting has already ended!');
      } else {
        setError(`Error voting: ${error.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const formatTimeRemaining = (seconds) => {
    if (seconds <= 0) return 'Voting has ended';

    const days = Math.floor(seconds / (24 * 3600));
    const hours = Math.floor((seconds % (24 * 3600)) / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (days > 0) {
      return `${days}d ${hours}h ${minutes}m remaining`;
    } else if (hours > 0) {
      return `${hours}h ${minutes}m remaining`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s remaining`;
    } else {
      return `${secs}s remaining`;
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">
          <div className="spinner"></div>
          <h2>Loading Advanced Voting System...</h2>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error">
          <h2>⚠️ Error</h2>
          <p>{error}</p>
          <button onClick={init} className="btn btn-primary">🔄 Retry</button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <header className="header">
        <h1>🗳️ Advanced Decentralized Voting System</h1>
        <div className="header-info">
          <div className="account-info">
            <p><strong>📱 Connected Account:</strong> {account.slice(0, 6)}...{account.slice(-4)}</p>
            <p><strong>👤 Role:</strong> {isOwner ? '👑 Owner' : '🗳️ Voter'}</p>
            <p><strong>🎯 Network:</strong> Ganache Local</p>
          </div>
          <div className="voting-status">
            <p><strong>Session:</strong> {currentSession?.title || 'No active session'}</p>
            <p><strong>Status:</strong> 
              <span className={votingActive ? 'status-active' : 'status-inactive'}>
                {votingActive ? '🟢 Active' : '🔴 Inactive'}
              </span>
            </p>
            <p><strong>Total Votes:</strong> {totalVotes}</p>
            {votingActive && timeRemaining > 0 && (
              <p><strong>⏰ Time:</strong> {formatTimeRemaining(timeRemaining)}</p>
            )}
          </div>
        </div>
      </header>

      {/* Owner Controls */}
      {isOwner && (
        <section className="owner-section">
          <h2>👑 Owner Controls</h2>
          <div className="owner-controls">
            <button 
              onClick={() => setShowOwnerPortal(true)}
              className="btn btn-primary btn-large"
            >
              🎛️ Open Owner Portal
            </button>
          </div>
          <p className="owner-info">
            Access the full owner portal to manage candidates, voting sessions, and view analytics.
          </p>
        </section>
      )}

      {/* Session Info */}
      {currentSession && (
        <section className="session-info-section">
          <h2>📋 Current Session</h2>
          <div className="session-info-card">
            <h3>{currentSession.title}</h3>
            <p>{currentSession.description}</p>
            <div className="session-meta">
              <span>Session ID: {currentSession.id}</span>
              <span>Total Votes: {currentSession.totalVotes}</span>
            </div>
          </div>
        </section>
      )}

      {/* Voter Registration */}
      {!isRegistered && !isOwner && (
        <section className="registration-section">
          <h2>📝 Voter Registration</h2>
          <p>You need to register before you can participate in this voting session.</p>
          <button 
            onClick={registerSelf}
            disabled={loading}
            className="btn btn-primary btn-large"
          >
            📝 Register to Vote
          </button>
        </section>
      )}

      {/* Current Winner */}
      {winner.name && winner.name !== "No votes cast" && totalVotes > 0 && (
        <section className="winner-section">
          <h2>🏆 Current Leader</h2>
          <div className="winner">
            <h3>🎖️ {winner.name}</h3>
            <p>{winner.voteCount} votes ({totalVotes > 0 ? Math.round((winner.voteCount / totalVotes) * 100) : 0}%)</p>
          </div>
        </section>
      )}

      {/* Voting Status Messages */}
      {!votingActive && (
        <section className="message-section">
          <div className="message message-warning">
            ⏸️ Voting is currently paused. Please wait for the owner to start the voting session.
          </div>
        </section>
      )}

      {votingActive && timeRemaining <= 0 && (
        <section className="message-section">
          <div className="message message-info">
            ⏰ Voting time has ended. Please wait for results to be finalized.
          </div>
        </section>
      )}

      {/* Candidates Section */}
      <section className="candidates-section">
        <h2>👥 Candidates</h2>

        {candidates.length === 0 ? (
          <div className="no-candidates">
            <h3>No candidates available</h3>
            <p>Please wait while candidates are being loaded or contact the administrator.</p>
          </div>
        ) : (
          <div className="candidates-grid">
            {candidates.map((candidate, index) => (
              <div key={candidate.id} className="candidate-card enhanced">
                <div className="candidate-header">
                  <h3>👤 {candidate.name}</h3>
                  <div className="vote-count">
                    <span className="votes">{candidate.voteCount}</span>
                    <span className="votes-label">votes</span>
                  </div>
                </div>

                {candidate.description && (
                  <p className="candidate-description">{candidate.description}</p>
                )}

                <div className="vote-percentage">
                  {totalVotes > 0 ? Math.round((candidate.voteCount / totalVotes) * 100) : 0}%
                </div>

                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{ 
                      width: `${totalVotes > 0 ? (candidate.voteCount / totalVotes) * 100 : 0}%` 
                    }}
                  ></div>
                </div>

                <div className="candidate-actions">
                  {isRegistered && !hasVoted && votingActive && timeRemaining > 0 && (
                    <button 
                      onClick={() => vote(candidate.id)}
                      disabled={loading}
                      className="btn btn-vote"
                    >
                      🗳️ Vote for {candidate.name}
                    </button>
                  )}

                  {hasVoted && (
                    <div className="voted-message">
                      ✅ Thank you for voting!
                    </div>
                  )}

                  {!votingActive && (
                    <div className="inactive-message">
                      ⏸️ Voting is currently paused
                    </div>
                  )}

                  {votingActive && timeRemaining <= 0 && (
                    <div className="inactive-message">
                      ⏰ Voting time has ended
                    </div>
                  )}

                  {!isRegistered && !isOwner && (
                    <div className="register-message">
                      📝 Register to vote
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* User Status */}
      <section className="status-section">
        <h2>📊 Your Status</h2>
        <div className="status-grid">
          <div className="status-card">
            <h4>📝 Registration</h4>
            <p className={isRegistered ? 'status-yes' : 'status-no'}>
              {isRegistered ? '✅ Registered' : '❌ Not Registered'}
            </p>
          </div>
          <div className="status-card">
            <h4>🗳️ Voting</h4>
            <p className={hasVoted ? 'status-yes' : 'status-no'}>
              {hasVoted ? '✅ Voted' : '❌ Not Voted'}
            </p>
          </div>
          <div className="status-card">
            <h4>⚡ System</h4>
            <p className={votingActive ? 'status-yes' : 'status-no'}>
              {votingActive ? '🟢 Active' : '🔴 Inactive'}
            </p>
          </div>
          <div className="status-card">
            <h4>⏰ Time</h4>
            <p className={timeRemaining > 0 ? 'status-yes' : 'status-no'}>
              {timeRemaining > 0 ? '✅ Available' : '❌ Ended'}
            </p>
          </div>
        </div>
      </section>

      <footer className="footer">
        <p>🔗 Connected to Advanced Voting System on Ganache (Chain ID: 1337)</p>
        <p>💡 Enhanced with scheduling, candidate management, and session history</p>
        <p>Version 2.0 - Full Owner Portal Integration</p>
      </footer>

      {/* Owner Portal Modal */}
      {showOwnerPortal && (
        <OwnerPortal 
          contract={contract}
          account={account}
          onClose={() => setShowOwnerPortal(false)}
        />
      )}
    </div>
  );
}

export default App;
